class bcolors:
    OK = '\033[92m' #GREEN
    WARNING = '\033[93m' #YELLOW
    ATTENTION = '\033[91m' #RED
    RESET = '\033[0m' #RESET COLOR

def print_malapi_matches(imports):
	malapi = defaultdict(set)
	has_matched = False
	for category in malapi_info.keys():
		for imp in imports:
			for f in imp.get('imported_functions'):
				if f in malapi_info[category]:
					malapi[category].add(f)
					has_matched = True
	if not has_matched:
		return
	print('malapi matches')
	for category in malapi.keys():
		if len(malapi[category]) == 0:
			continue
		print(f'{colors.ATTENTION}\t' + category + f'{colors.RESET}: ', end='')
		print(*malapi[category], sep=' | ')
		print()
	print()